import React from 'react';
import { Card, CardBody } from "reactstrap";
import { Link } from 'react-router-dom';
import calculateAvgRating from '../utils/avgRating';
import './tour-card.css';

const TourCard = ({ tour }) => {
  const { _id, title, city, photo, price, featured, reviews } = tour;
  const { totalRating, avgRating } = calculateAvgRating(reviews);

  return (
    <div className="tour__card">
      <Card>
        <div className="tour__img">
          <img src={photo} alt="tour-img" />
          {featured && <span>Featured</span>}
        </div>

        <CardBody>
          <div className="card__top d-flex justify-content-between">
            <span className="tour__location d-flex align-items-center ">
              <i className="ri-map-pin-line"></i>{city}
            </span>
            <span className="tour__rating align-items-center">
              <i className="ri-star-s-fill"></i>
              {avgRating === 0 ? 'Not rated' : avgRating}
              <span>({reviews.length})</span>
            </span>
          </div>

          <h5 className="tour__title"><Link to={`/tours/${_id}`}>{title}</Link></h5>

          <div className="card__bottom">
            <h5>â‚¹{price} <span>/per person</span></h5>
            <button className="btn booking__btn">
              <Link to={`/tours/${_id}`}>Book Now</Link>
            </button>
          </div>
        </CardBody>
      </Card>
    </div>
  );
};

export default TourCard;
